import React, { useState, useEffect } from 'react';
import './newOrder.css';
import Header from './header';
import axios from 'axios';
import apis from '../utils/api';
const NewOrder = () => {
  const [currentDateTime] = useState(new Date().toLocaleString());
  const [selectedPayment, setSelectedPayment] = useState('');
  const [selectedDueTime, setSelectedDueTime] = useState('');
  const [customDueTime, setCustomDueTime] = useState('');
  const [designImages, setDesignImages] = useState([]);
  const [customer, setCustomer] = useState({
    name: '',
    phone: '',
    email: '',
    company: ''
  });
  const [items, setItems] = useState([
    {
      id: '1',
      barcode: '',
      itemNumber: '',
      color: '',
      price: '',
      cost: '',
      quantity: '',
      selectedColors: [],
      selectedPlacements: []
    }
  ]);
// Use these helper functions to call your backend APIs

// 1. Create an item and return its id
const createItem = async (itemData) => {
  const res = await axios.post(apis().newItem, itemData);
  return res.data.item._id;
};

// 2. Create a design and return its id
const createDesign = async (designData) => {
  const res = await axios.post(apis().newDesign, designData);
  return res.data.design._id;
};

// 3. Create a customer and return its id
const createCustomer = async (customerData) => {
  const res = await axios.post(apis().newCustomer, customerData);
  return res.data.customer._id;
};

// 4. Create a payment and return its id
const createPayment = async (paymentData) => {
  const res = await axios.post(apis().newOrderPayment, paymentData);
  return res.data.payment._id;
};

// 5. (Optional) Upload images separately if needed; expect form-data and URL returns
const uploadImages = async (formData) => {
  const res = await axios.post(apis().images, formData);
  return res.data.uploadedPaths; // adjust per your API response
};

// 6. Create the final order using the collected IDs
const createFinalOrder = async (orderData) => {
  const res = await axios.post(apis().newOrder, orderData);
  return res.data.order._id;
};

  // Modal state for color selection
  const [showColorModal, setShowColorModal] = useState(false);
  const [itemForColorModal, setItemForColorModal] = useState(null);

  // Modal state for checkout
  const [showCheckoutModal, setShowCheckoutModal] = useState(false);

  const openColorModal = (itemId) => {
    setItemForColorModal(itemId);
    setShowColorModal(true);
  };

  const closeColorModal = () => {
    setShowColorModal(false);
    setItemForColorModal(null);
  };

  const colorChart = [
    { hex: '#000000', name: 'Black' },
    { hex: '#0B0B0B', name: 'Very Dark Gray' }
  ];

  const handlePlacementClick = (itemId, placement) => {
    setItems(prevItems =>
      prevItems.map(item => {
        if (item.id === itemId) {
          const newPlacements = item.selectedPlacements.includes(placement)
            ? item.selectedPlacements.filter(p => p !== placement)
            : [...item.selectedPlacements, placement];
          return { ...item, selectedPlacements: newPlacements };
        }
        return item;
      })
    );
  };

  const handleColorSelect = (itemId, hex) => {
    setItems(prevItems =>
      prevItems.map(item => {
        if (item.id === itemId) {
          const newColors = item.selectedColors.includes(hex)
            ? item.selectedColors.filter(c => c !== hex)
            : [...item.selectedColors, hex];
          return { ...item, selectedColors: newColors };
        }
        return item;
      })
    );
  };

  const handleItemChange = (itemId, field, value) => {
    setItems(prevItems =>
      prevItems.map(item =>
        item.id === itemId ? { ...item, [field]: value } : item
      )
    );
  };

  const addItem = () => {
    const newId = (parseInt(items[items.length - 1].id) + 1).toString();
    setItems(prev => [
      ...prev,
      {
        id: newId,
        barcode: '',
        itemNumber: '',
        color: '',
        price: '',
        cost: '',
        quantity: '',
        selectedColors: [],
        selectedPlacements: []
      }
    ]);
  };

  const removeItem = (itemId) => {
    if (items.length > 1) {
      setItems(prev => prev.filter(item => item.id !== itemId));
    }
  };

  const handleDesignImagesChange = (e) => {
    const files = Array.from(e.target.files).map((file) => ({
      file,
      preview: URL.createObjectURL(file),
    }));
    setDesignImages((prev) => [...prev, ...files]);
  };

  const handleDueTimeSelect = (time) => {
    setSelectedDueTime(time);
    const now = new Date();
    let dueDate = new Date(now);
    switch (time) {
      case '15min':
        dueDate.setMinutes(now.getMinutes() + 15);
        break;
      case '20min':
        dueDate.setMinutes(now.getMinutes() + 20);
        break;
      case '30min':
        dueDate.setMinutes(now.getMinutes() + 30);
        break;
      case '1hour':
        dueDate.setHours(now.getHours() + 1);
        break;
      default:
        break;
    }
    setCustomDueTime(dueDate.toISOString().slice(0, 16));
  };

  useEffect(() => {
    let buffer = '';
    let lastKeyTime = Date.now();

    const handleKeyPress = (e) => {
      const currentTime = Date.now();
      if (currentTime - lastKeyTime > 100) {
        buffer = '';
      }
      if (e.key === 'Enter') {
        if (buffer) {
          handleItemChange(items[0].id, 'barcode', buffer);
          buffer = '';
        }
      } else {
        buffer += e.key;
      }
      lastKeyTime = currentTime;
    };

    window.addEventListener('keypress', handleKeyPress);
    return () => window.removeEventListener('keypress', handleKeyPress);
  }, [items]);

  const fetchCustomerByPhone = async (phone) => {
    const mockDB = {
      '1234567890': { name: 'John Doe', email: 'john@example.com', company: 'Acme Inc.' },
      '9876543210': { name: 'Jane Smith', email: 'jane@example.com', company: 'Globex Corp.' }
    };
    return mockDB[phone] || null;
  };

  const handlePhoneBlur = async (e) => {
    const phone = e.target.value;
    if (phone) {
      const result = await fetchCustomerByPhone(phone);
      if (result) {
        setCustomer(prev => ({ ...prev, phone, ...result }));
      }
    }
  };

  const handleCustomerChange = (field, value) => {
    setCustomer(prev => ({ ...prev, [field]: value }));
  };

  // Calculate total price (price × quantity for each item)
  const calculateTotalPrice = () => {
    return items.reduce((sum, item) => {
      const price = parseFloat(item.price) || 0;
      const quantity = parseFloat(item.quantity) || 1;
      return sum + price * quantity;
    }, 0);
  };

  // Instead of a checkout section on the main page,
  // the form submission triggers the checkout popup
  const handleSubmit = (e) => {
    e.preventDefault();
    setShowCheckoutModal(true);
  };

  // Modify your handlePlaceOrder function as follows:
const handlePlaceOrder = async () => {
  try {
    // 1. Create each item and collect IDs
    const itemIds = await Promise.all(
      items.map(async (item) => {
        return await createItem({
          name: item.itemNumber,
          color: item.color,
          price: item.price,
          cost: item.cost,
          quantity: item.quantity,
          // Add 'picture' if available (e.g., if using uploadImages function)
        });
      })
    );

    // 2. Create design — build your payload based on your design state; below is an example payload.
    // You may need to include images URLs from a separate upload process.
    const designId = await createDesign({
      name: "Summer Collection",    // customize
      pictures: [
        // { pictureFiles: ["http://example.com/pic1.jpg", ...], colorChart: [{ item: "T-shirt", color: "Blue" }], placement: [{ position1: "A1", position2: "H1" }], size: "FILL" }
      ],
      text: [
        // { font: "Arial", color: [{ item: "Banner", color: "Red" }], placement: [{ position1: "A2", position2: "H2" }], size: "FILL" }
      ]
    });

    // 3. Create customer
    const customerId = await createCustomer({
      name: customer.name,
      number: customer.phone,
      email: customer.email,
      company: customer.company
    });

    // 4. Create payment; note that you can adjust boolean flags as needed.
    const paymentId = await createPayment({
      method: selectedPayment.toUpperCase(),
      amount: calculateTotalPrice(),
      layaway: selectedPayment === 'layaway',
      quote: selectedPayment === 'quote'
    });

    // 5. Finally create the order with collected IDs:
    const orderPayload = {
      items: itemIds,              // items ids array
      design: designId,            // design id
      customer: customerId,        // customer id
      payments: [paymentId],       // array with payment id(s)
      createdBy: "60f4f4d5eb7a8a56acde1238", // or fetch from current user context
      duedate: customDueTime       // ensure proper date format (ISO string ideally)
    };

    await createFinalOrder(orderPayload);

    // Close modal and inform the user
    setShowCheckoutModal(false);
    alert('Order placed successfully!');
  } catch (error) {
    console.error('Error placing order:', error);
    alert('Error placing order');
  }
};

  return (
    <>
      <Header />
      <div className="new-order-container">
        <form onSubmit={handleSubmit}>
          <div className="global-controls">
            <button type="button" className="add-item-button" onClick={addItem}>
              Add New Item
            </button>
          </div>

          <div className="section">
            <h2 className="section-title">Date/Time</h2>
            <div className="form-group">
              <input type="text" className="form-input" value={currentDateTime} readOnly />
            </div>
          </div>

          {items.map((item, index) => (
            <div key={item.id}>
              <div className="section">
                <div className="item-number">Item #{index + 1}</div>
                {items.length > 1 && (
                  <button type="button" className="remove-button" onClick={() => removeItem(item.id)}>
                    Remove
                  </button>
                )}
                <h2 className="section-title">Item</h2>
                <div className="form-group">
                  <label className="form-label">POS Scan</label>
                  <div className="scanner-container">
                    <input
                      type="text"
                      className="form-input scanner-input"
                      value={item.barcode}
                      onChange={(e) => handleItemChange(item.id, 'barcode', e.target.value)}
                      placeholder="Scan barcode or enter manually"
                    />
                    <svg className="scanner-icon" viewBox="0 0 24 24" fill="none" stroke="currentColor">
                      <path
                        strokeLinecap="round"
                        strokeLinejoin="round"
                        strokeWidth={2}
                        d="M12 4v1m6 11h2m-6 0h-2v4m0-11v3m0 0h.01M12 12h4.01M16 20h4M4 12h4m12 0h.01M5 8h2a1 1 0 001-1V5a1 1 0 00-1-1H5a1 1 0 00-1 1v2a1 1 0 001 1zm12 0h2a1 1 0 001-1V5a1 1 0 00-1-1h-2a1 1 0 00-1 1v2a1 1 0 001 1zM5 20h2a1 1 0 001-1v-2a1 1 0 00-1-1H5a1 1 0 00-1 1v2a1 1 0 001 1z"
                      />
                    </svg>
                  </div>
                </div>

                <div className="form-group">
                  <h3 className="section-title">New Item</h3>
                  <div className="grid-container">
                    <input
                      type="text"
                      className="form-input"
                      placeholder="Item Number"
                      value={item.itemNumber}
                      onChange={(e) => handleItemChange(item.id, 'itemNumber', e.target.value)}
                    />
                    <input
                      type="text"
                      className="form-input"
                      placeholder="Color"
                      value={item.color}
                      onChange={(e) => handleItemChange(item.id, 'color', e.target.value)}
                    />
                    <input
                      type="number"
                      className="form-input"
                      placeholder="Price"
                      value={item.price}
                      onChange={(e) => handleItemChange(item.id, 'price', e.target.value)}
                    />
                    <input
                      type="number"
                      className="form-input"
                      placeholder="Cost"
                      value={item.cost}
                      onChange={(e) => handleItemChange(item.id, 'cost', e.target.value)}
                    />
                    <input
                      type="number"
                      className="form-input"
                      placeholder="Quantity"
                      value={item.quantity}
                      onChange={(e) => handleItemChange(item.id, 'quantity', e.target.value)}
                    />
                  </div>
                  <div className="upload-area">
  <p>Upload 8x8 Design File</p>
  <input 
    type="file" 
    accept="image/*" 
    multiple 
    capture="environment" 
    onChange={handleDesignImagesChange} 
  />
</div>
                </div>
              </div>

              <div className="section">
                <h2 className="section-title">Design</h2>
                <div className="upload-area">
  <p>Upload 8x8 Design File</p>
  <input 
    type="file" 
    accept="image/*" 
    multiple 
    capture="environment" 
    onChange={handleDesignImagesChange} 
  />
</div>

                <div className="form-group">
                  <label className="form-label">Color Selection</label>
                  <button
                    type="button"
                    className="open-color-modal-button"
                    onClick={() => openColorModal(item.id)}
                  >
                    Select Colors
                  </button>
                  {item.selectedColors.length > 0 && (
                    <div className="selected-colors">
                      {item.selectedColors.map(color => (
                        <div key={color} className="selected-color-pill">
                          <div
                            className="selected-color-swatch"
                            style={{ backgroundColor: color }}
                          />
                          {colorChart.find(c => c.hex === color)?.name}
                        </div>
                      ))}
                    </div>
                  )}
                </div>

                <div className="form-group">
                  <label className="form-label">Placement</label>
                  <div className="placement-grid">
                    {[...Array(9)].map((_, i) => (
                      <div
                        key={`A${i + 1}`}
                        className={`placement-cell ${item.selectedPlacements.includes(`A${i + 1}`) ? 'selected' : ''}`}
                        onClick={() => handlePlacementClick(item.id, `A${i + 1}`)}
                      >
                        A{i + 1}
                      </div>
                    ))}
                    {[...Array(9)].map((_, i) => (
                      <div
                        key={`H${i + 1}`}
                        className={`placement-cell ${item.selectedPlacements.includes(`H${i + 1}`) ? 'selected' : ''}`}
                        onClick={() => handlePlacementClick(item.id, `H${i + 1}`)}
                      >
                        H{i + 1}
                      </div>
                    ))}
                  </div>
                </div>
                <div className="section">
            <h2 className="section-title">Text</h2>
            <div className="form-group">
              <label className="form-label">Font</label>
              <select className="form-select">
                <option>Arial</option>
                <option>Times New Roman</option>
                <option>Helvetica</option>
              </select>
            </div>
          </div>
              </div>
              <hr />
            </div>
          ))}

          

          <div className="section">
            <h2 className="section-title">Due</h2>
            <div className="time-buttons">
              <button
                type="button"
                className={`time-button ${selectedDueTime === '15min' ? 'selected' : ''}`}
                onClick={() => handleDueTimeSelect('15min')}
              >
                15 min
              </button>
              <button
                type="button"
                className={`time-button ${selectedDueTime === '20min' ? 'selected' : ''}`}
                onClick={() => handleDueTimeSelect('20min')}
              >
                20 min
              </button>
              <button
                type="button"
                className={`time-button ${selectedDueTime === '30min' ? 'selected' : ''}`}
                onClick={() => handleDueTimeSelect('30min')}
              >
                30 min
              </button>
              <button
                type="button"
                className={`time-button ${selectedDueTime === '1hour' ? 'selected' : ''}`}
                onClick={() => handleDueTimeSelect('1hour')}
              >
                1 hour
              </button>
              <input
                type="datetime-local"
                className="form-input"
                value={customDueTime}
                onChange={(e) => {
                  setCustomDueTime(e.target.value);
                  setSelectedDueTime('custom');
                }}
              />
            </div>
          </div>

          <div className="section">
            <h2 className="section-title">Customer Information</h2>
            <div className="grid-container">
              <input
                type="text"
                className="form-input"
                placeholder="Name"
                value={customer.name}
                onChange={(e) => handleCustomerChange('name', e.target.value)}
              />
              <input
                type="tel"
                className="form-input"
                placeholder="Phone Number"
                value={customer.phone}
                onChange={(e) => handleCustomerChange('phone', e.target.value)}
                onBlur={handlePhoneBlur}
              />
              <input
                type="email"
                className="form-input"
                placeholder="Email Address"
                value={customer.email}
                onChange={(e) => handleCustomerChange('email', e.target.value)}
              />
              <input
                type="text"
                className="form-input"
                placeholder="Company"
                value={customer.company}
                onChange={(e) => handleCustomerChange('company', e.target.value)}
              />
            </div>
          </div>

          <div className="section">
            <h2 className="section-title">Payment</h2>
            <div className="payment-options">
              <div
                className={`payment-option ${selectedPayment === 'cash' ? 'selected' : ''}`}
                onClick={() => setSelectedPayment('cash')}
              >
                Cash
              </div>
              <div
                className={`payment-option ${selectedPayment === 'card' ? 'selected' : ''}`}
                onClick={() => setSelectedPayment('card')}
              >
                Card
              </div>
              <div
                className={`payment-option ${selectedPayment === 'check' ? 'selected' : ''}`}
                onClick={() => setSelectedPayment('check')}
              >
                Check
              </div>
              <div
                className={`payment-option ${selectedPayment === 'layaway' ? 'selected' : ''}`}
                onClick={() => setSelectedPayment('layaway')}
              >
                Layaway (50%)
              </div>
              <div
                className={`payment-option ${selectedPayment === 'quote' ? 'selected' : ''}`}
                onClick={() => setSelectedPayment('quote')}
              >
                Quote
              </div>
            </div>
          </div>

          {/* Instead of an in-form checkout section, the form submission triggers the checkout popup */}
          <button type="submit" className="submit-button">
            Review Order
          </button>
        </form>
      </div>

      {/* Color Modal */}
      {showColorModal && (
        <div className="color-modal">
          <div className="color-modal-content">
            <button type="button" className="close-button" onClick={closeColorModal}>
              X
            </button>
            <div className="color-chart">
              {colorChart.map(color => (
                <div
                  key={color.hex}
                  className={`color-swatch ${
                    items.find(i => i.id === itemForColorModal)?.selectedColors.includes(color.hex)
                      ? 'selected'
                      : ''
                  }`}
                  style={{ backgroundColor: color.hex }}
                  onClick={() => handleColorSelect(itemForColorModal, color.hex)}
                  title={color.name}
                />
              ))}
            </div>
          </div>
        </div>
      )}

      {/* Checkout Modal Popup */}
      {showCheckoutModal && (
        <div className="checkout-modal">
          <div className="checkout-modal-content">
            <button type="button" className="close-button" onClick={() => setShowCheckoutModal(false)}>
              X
            </button>
            <h2>Checkout</h2>
            <div className="checkout-items">
              {items.map(item => {
                const price = parseFloat(item.price) || 0;
                const quantity = parseFloat(item.quantity) || 1;
                const amount = (price * quantity).toFixed(2);
                return (
                  <div key={item.id} className="checkout-item">
                    <p>
                      {item.itemNumber || `Item ${item.id}`}: ${amount}
                    </p>
                  </div>
                );
              })}
            </div>
            <p className="total-price">Total Price: ${calculateTotalPrice().toFixed(2)}</p>
            <button type="button" className="place-order-button" onClick={handlePlaceOrder}>
              Place Order
            </button>
          </div>
        </div>
      )}
    </>
  );
};

export default NewOrder;
