import React, { useState, useEffect } from 'react';
import './NewOrder.css';

const NewOrder = () => {
  const [currentDateTime] = useState(new Date().toLocaleString());
  const [selectedPayment, setSelectedPayment] = useState('');
  const [selectedPlacements, setSelectedPlacements] = useState([]);
  const [selectedColors, setSelectedColors] = useState([]);
  const [barcode, setBarcode] = useState('');
  
  // Comprehensive color chart
  const colorChart = [
    { hex: '#000000', name: 'Black' },
    { hex: '#FFFFFF', name: 'White' },
    { hex: '#FF0000', name: 'Red' },
    { hex: '#00FF00', name: 'Green' },
    { hex: '#0000FF', name: 'Blue' },
    { hex: '#FFFF00', name: 'Yellow' },
    { hex: '#FF00FF', name: 'Magenta' },
    { hex: '#00FFFF', name: 'Cyan' },
    { hex: '#FFA500', name: 'Orange' },
    { hex: '#800080', name: 'Purple' },
    { hex: '#008000', name: 'Dark Green' },
    { hex: '#800000', name: 'Maroon' },
    { hex: '#808000', name: 'Olive' },
    { hex: '#008080', name: 'Teal' },
    { hex: '#FFC0CB', name: 'Pink' },
    { hex: '#A52A2A', name: 'Brown' },
    { hex: '#808080', name: 'Gray' },
    { hex: '#C0C0C0', name: 'Silver' },
    { hex: '#FFD700', name: 'Gold' },
    { hex: '#4B0082', name: 'Indigo' }
  ];

  const handlePlacementClick = (placement) => {
    setSelectedPlacements(prev => 
      prev.includes(placement)
        ? prev.filter(p => p !== placement)
        : [...prev, placement]
    );
  };

  const handleColorSelect = (hex) => {
    setSelectedColors(prev => 
      prev.includes(hex)
        ? prev.filter(c => c !== hex)
        : [...prev, hex]
    );
  };

  // Handle barcode scanning
  useEffect(() => {
    let buffer = '';
    let lastKeyTime = Date.now();

    const handleKeyPress = (e) => {
      const currentTime = Date.now();
      
      // If there's a delay longer than 100ms between keystrokes, reset the buffer
      if (currentTime - lastKeyTime > 100) {
        buffer = '';
      }
      
      if (e.key === 'Enter') {
        if (buffer) {
          setBarcode(buffer);
          buffer = '';
        }
      } else {
        buffer += e.key;
      }
      
      lastKeyTime = currentTime;
    };

    window.addEventListener('keypress', handleKeyPress);
    return () => window.removeEventListener('keypress', handleKeyPress);
  }, []);

  const handleSubmit = (e) => {
    e.preventDefault();
    // Handle form submission to backend
  };

  return (
    <div className="new-order-container">
      <form onSubmit={handleSubmit}>
        {/* Date/Time Section */}
        <div className="section">
          <h2 className="section-title">Date/Time</h2>
          <div className="form-group">
            <input 
              type="text" 
              className="form-input" 
              value={currentDateTime} 
              readOnly 
            />
          </div>
        </div>

        {/* Item Section */}
        <div className="section">
          <h2 className="section-title">Item</h2>
          <div className="form-group">
            <label className="form-label">POS Scan</label>
            <div className="scanner-container">
              <input 
                type="text" 
                className="form-input scanner-input"
                value={barcode}
                onChange={(e) => setBarcode(e.target.value)}
                placeholder="Scan barcode or enter manually"
              />
              <svg className="scanner-icon" viewBox="0 0 24 24" fill="none" stroke="currentColor">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 4v1m6 11h2m-6 0h-2v4m0-11v3m0 0h.01M12 12h4.01M16 20h4M4 12h4m12 0h.01M5 8h2a1 1 0 001-1V5a1 1 0 00-1-1H5a1 1 0 00-1 1v2a1 1 0 001 1zm12 0h2a1 1 0 001-1V5a1 1 0 00-1-1h-2a1 1 0 00-1 1v2a1 1 0 001 1zM5 20h2a1 1 0 001-1v-2a1 1 0 00-1-1H5a1 1 0 00-1 1v2a1 1 0 001 1z" />
              </svg>
            </div>
          </div>

          <div className="form-group">
            <h3 className="section-title">New Item</h3>
            <div className="grid-container">
              <input 
                type="text" 
                className="form-input" 
                placeholder="Item Number"
              />
              <input 
                type="text" 
                className="form-input" 
                placeholder="Color"
              />
              <input 
                type="number" 
                className="form-input" 
                placeholder="Price"
              />
              <input 
                type="number" 
                className="form-input" 
                placeholder="Cost"
              />
              <input 
                type="number" 
                className="form-input" 
                placeholder="Quantity"
              />
            </div>
            <div className="upload-area">
              <p>Click to upload picture or drag and drop</p>
              <input type="file" hidden />
            </div>
          </div>
        </div>

        {/* Design Section */}
        <div className="section">
          <h2 className="section-title">Design</h2>
          <div className="upload-area">
            <p>Upload 8x8 Design File</p>
            <input type="file" hidden />
          </div>

          <div className="form-group">
            <label className="form-label">Color Selection</label>
            <div className="color-chart">
              {colorChart.map(color => (
                <div
                  key={color.hex}
                  className={`color-swatch ${selectedColors.includes(color.hex) ? 'selected' : ''}`}
                  style={{ backgroundColor: color.hex }}
                  onClick={() => handleColorSelect(color.hex)}
                  title={color.name}
                />
              ))}
            </div>
            {selectedColors.length > 0 && (
              <div className="selected-colors">
                {selectedColors.map(color => (
                  <div key={color} className="selected-color-pill">
                    <div 
                      className="selected-color-swatch" 
                      style={{ backgroundColor: color }}
                    />
                    {colorChart.find(c => c.hex === color)?.name}
                  </div>
                ))}
              </div>
            )}
          </div>

          <div className="form-group">
            <label className="form-label">Placement</label>
            <div className="placement-grid">
              {[...Array(9)].map((_, i) => (
                <div
                  key={`A${i+1}`}
                  className={`placement-cell ${selectedPlacements.includes(`A${i+1}`) ? 'selected' : ''}`}
                  onClick={() => handlePlacementClick(`A${i+1}`)}
                >
                  A{i+1}
                </div>
              ))}
              {[...Array(9)].map((_, i) => (
                <div
                  key={`H${i+1}`}
                  className={`placement-cell ${selectedPlacements.includes(`H${i+1}`) ? 'selected' : ''}`}
                  onClick={() => handlePlacementClick(`H${i+1}`)}
                >
                  H{i+1}
                </div>
              ))}
            </div>
          </div>
        </div>

        {/* Text Section */}
        <div className="section">
          <h2 className="section-title">Text</h2>
          <div className="form-group">
            <label className="form-label">Font</label>
            <select className="form-select">
              <option>Arial</option>
              <option>Times New Roman</option>
              <option>Helvetica</option>
            </select>
          </div>
        </div>

        {/* Due Section */}
        <div className="section">
          <h2 className="section-title">Due</h2>
          <div className="time-buttons">
            <button type="button" className="time-button">15 min</button>
            <button type="button" className="time-button">20 min</button>
            <button type="button" className="time-button">30 min</button>
            <button type="button" className="time-button">1 hour</button>
            <input type="datetime-local" className="form-input" />
          </div>
        </div>

        {/* Customer Info Section */}
        <div className="section">
          <h2 className="section-title">Customer Information</h2>
          <div className="grid-container">
            <input 
              type="text" 
              className="form-input" 
              placeholder="Name"
            />
            <input 
              type="tel" 
              className="form-input" 
              placeholder="Phone Number"
            />
            <input 
              type="text" 
              className="form-input" 
              placeholder="Company"
            />
          </div>
        </div>

        {/* Payment Section */}
        <div className="section">
          <h2 className="section-title">Payment</h2>
          <div className="payment-options">
            <div 
              className={`payment-option ${selectedPayment === 'cash' ? 'selected' : ''}`}
              onClick={() => setSelectedPayment('cash')}
            >
              Cash
            </div>
            <div 
              className={`payment-option ${selectedPayment === 'card' ? 'selected' : ''}`}
              onClick={() => setSelectedPayment('card')}
            >
              Card
            </div>
            <div 
              className={`payment-option ${selectedPayment === 'check' ? 'selected' : ''}`}
              onClick={() => setSelectedPayment('check')}
            >
              Check
            </div>
            <div 
              className={`payment-option ${selectedPayment === 'layaway' ? 'selected' : ''}`}
              onClick={() => setSelectedPayment('layaway')}
            >
              Layaway (50%)
            </div>
            <div 
              className={`payment-option ${selectedPayment === 'quote' ? 'selected' : ''}`}
              onClick={() => setSelectedPayment('quote')}
            >
              Quote
            </div>
          </div>
        </div>

        {/* Print Section */}
        <div className="print-buttons">
          <button type="button" className="print-button">Print Receipt</button>
          <button type="button" className="print-button">Print Barcode</button>
          <button type="button" className="print-button">Print Order Form</button>
        </div>
      </form>
    </div>
  );
};

export default NewOrder;
