import React, { useState, useEffect } from 'react';
import './newOrder.css';
import Header from './header';

const NewOrder = () => {
  const [currentDateTime] = useState(new Date().toLocaleString());
  const [selectedPayment, setSelectedPayment] = useState('');
  const [selectedDueTime, setSelectedDueTime] = useState('');
  const [customDueTime, setCustomDueTime] = useState('');
  const [customer, setCustomer] = useState({
    name: '',
    phone: '',
    email: '',
    company: ''
  });
  const [items, setItems] = useState([
    {
      id: '1',
      barcode: '',
      itemNumber: '',
      color: '',
      price: '',
      cost: '',
      quantity: '',
      selectedColors: [],
      selectedPlacements: []
    }
  ]);
  const [designs, setDesigns] = useState([
    {
      id: '1',
      designFile: '',
      selectedColors: [],
      selectedPlacements: []
    }
  ]);

  // Modal state for color selection
  const [showColorModal, setShowColorModal] = useState(false);
  // modalFor indicates whether we're selecting colors for an item or a design
  const [modalFor, setModalFor] = useState(null); // 'item' or 'design'
  const [elementIdForModal, setElementIdForModal] = useState(null);

  const openColorModal = (forType, elementId) => {
    setModalFor(forType);
    setElementIdForModal(elementId);
    setShowColorModal(true);
  };

  const closeColorModal = () => {
    setShowColorModal(false);
    setModalFor(null);
    setElementIdForModal(null);
  };

  const colorChart = [
    { hex: '#000000', name: 'Black' },
    { hex: '#0B0B0B', name: 'Very Dark Gray' },
  ];

  // Handlers for Items
  const handleItemPlacementClick = (itemId, placement) => {
    setItems(prevItems =>
      prevItems.map(item => {
        if (item.id === itemId) {
          const newPlacements = item.selectedPlacements && item.selectedPlacements.includes(placement)
            ? item.selectedPlacements.filter(p => p !== placement)
            : [...(item.selectedPlacements || []), placement];
          return { ...item, selectedPlacements: newPlacements };
        }
        return item;
      })
    );
  };

  const handleItemColorSelect = (itemId, hex) => {
    setItems(prevItems =>
      prevItems.map(item => {
        if (item.id === itemId) {
          const newColors = item.selectedColors && item.selectedColors.includes(hex)
            ? item.selectedColors.filter(c => c !== hex)
            : [...(item.selectedColors || []), hex];
          return { ...item, selectedColors: newColors };
        }
        return item;
      })
    );
  };

  const handleItemChange = (itemId, field, value) => {
    setItems(prevItems =>
      prevItems.map(item =>
        item.id === itemId ? { ...item, [field]: value } : item
      )
    );
  };

  const addItem = () => {
    const newId = (parseInt(items[items.length - 1].id) + 1).toString();
    setItems(prev => [
      ...prev,
      {
        id: newId,
        barcode: '',
        itemNumber: '',
        color: '',
        price: '',
        cost: '',
        quantity: '',
        selectedColors: [],
        selectedPlacements: []
      }
    ]);
  };

  // Always show remove button for each item
  const removeItem = (itemId) => {
    setItems(prev => prev.filter(item => item.id !== itemId));
  };

  // Handlers for Designs
  const handleDesignPlacementClick = (designId, placement) => {
    setDesigns(prevDesigns =>
      prevDesigns.map(design => {
        if (design.id === designId) {
          const newPlacements = design.selectedPlacements && design.selectedPlacements.includes(placement)
            ? design.selectedPlacements.filter(p => p !== placement)
            : [...(design.selectedPlacements || []), placement];
          return { ...design, selectedPlacements: newPlacements };
        }
        return design;
      })
    );
  };

  const handleDesignColorSelect = (designId, hex) => {
    setDesigns(prevDesigns =>
      prevDesigns.map(design => {
        if (design.id === designId) {
          const newColors = design.selectedColors && design.selectedColors.includes(hex)
            ? design.selectedColors.filter(c => c !== hex)
            : [...(design.selectedColors || []), hex];
          return { ...design, selectedColors: newColors };
        }
        return design;
      })
    );
  };

  const handleDesignFileChange = (designId, file) => {
    // For simplicity, we'll just store the file name
    setDesigns(prevDesigns =>
      prevDesigns.map(design =>
        design.id === designId ? { ...design, designFile: file.name } : design
      )
    );
  };

  const addDesign = () => {
    const newId = (parseInt(designs[designs.length - 1].id) + 1).toString();
    setDesigns(prev => [
      ...prev,
      {
        id: newId,
        designFile: '',
        selectedColors: [],
        selectedPlacements: []
      }
    ]);
  };

  // Always show remove button for each design
  const removeDesign = (designId) => {
    setDesigns(prev => prev.filter(design => design.id !== designId));
  };

  const handleDueTimeSelect = (time) => {
    setSelectedDueTime(time);
    const now = new Date();
    let dueDate = new Date(now);
    switch (time) {
      case '15min':
        dueDate.setMinutes(now.getMinutes() + 15);
        break;
      case '20min':
        dueDate.setMinutes(now.getMinutes() + 20);
        break;
      case '30min':
        dueDate.setMinutes(now.getMinutes() + 30);
        break;
      case '1hour':
        dueDate.setHours(now.getHours() + 1);
        break;
      default:
        break;
    }
    setCustomDueTime(dueDate.toISOString().slice(0, 16));
  };

  useEffect(() => {
    let buffer = '';
    let lastKeyTime = Date.now();
    const handleKeyPress = (e) => {
      const currentTime = Date.now();
      if (currentTime - lastKeyTime > 100) {
        buffer = '';
      }
      if (e.key === 'Enter') {
        if (buffer) {
          handleItemChange(items[0].id, 'barcode', buffer);
          buffer = '';
        }
      } else {
        buffer += e.key;
      }
      lastKeyTime = currentTime;
    };
    window.addEventListener('keypress', handleKeyPress);
    return () => window.removeEventListener('keypress', handleKeyPress);
  }, [items]);

  const fetchCustomerByPhone = async (phone) => {
    const mockDB = {
      '1234567890': { name: 'John Doe', email: 'john@example.com', company: 'Acme Inc.' },
      '9876543210': { name: 'Jane Smith', email: 'jane@example.com', company: 'Globex Corp.' }
    };
    return mockDB[phone] || null;
  };

  const handlePhoneBlur = async (e) => {
    const phone = e.target.value;
    if (phone) {
      const result = await fetchCustomerByPhone(phone);
      if (result) {
        setCustomer(prev => ({ ...prev, phone, ...result }));
      }
    }
  };

  const handleCustomerChange = (field, value) => {
    setCustomer(prev => ({ ...prev, [field]: value }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log('Order data:', { items, designs, selectedPayment });
  };

  return (
    <>
      <Header />
      <div className="new-order-container">
        <form onSubmit={handleSubmit}>
          <div className="global-controls">
            <button type="button" className="add-item-button" onClick={addItem}>
              Add New Item
            </button>
          </div>

          {/* Items Section */}
          <div className="section">
            <h2 className="section-title">Items</h2>
            {items.map((item, index) => (
              <div key={item.id}>
                <div className="item-number">
                  Item #{index + 1}
                  <button type="button" className="remove-button" onClick={() => removeItem(item.id)}>X</button>
                </div>
                <div className="form-group">
                  <label className="form-label">POS Scan</label>
                  <div className="scanner-container">
                    <input
                      type="text"
                      className="form-input scanner-input"
                      value={item.barcode}
                      onChange={(e) => handleItemChange(item.id, 'barcode', e.target.value)}
                      placeholder="Scan barcode or enter manually"
                    />
                    <svg className="scanner-icon" viewBox="0 0 24 24" fill="none" stroke="currentColor">
                      <path
                        strokeLinecap="round"
                        strokeLinejoin="round"
                        strokeWidth={2}
                        d="M12 4v1m6 11h2m-6 0h-2v4m0-11v3m0 0h.01M12 12h4.01M16 20h4M4 12h4m12 0h.01M5 8h2a1 1 0 001-1V5a1 1 0 00-1-1H5a1 1 0 00-1 1v2a1 1 0 001 1zm12 0h2a1 1 0 001-1V5a1 1 0 00-1-1h-2a1 1 0 00-1 1v2a1 1 0 001 1zM5 20h2a1 1 0 001-1v-2a1 1 0 00-1-1H5a1 1 0 00-1 1v2a1 1 0 001 1z"
                      />
                    </svg>
                  </div>
                </div>
                <div className="form-group">
                  <label className="form-label">Item Details</label>
                  <div className="grid-container">
                    <input
                      type="text"
                      className="form-input"
                      placeholder="Item Number"
                      value={item.itemNumber}
                      onChange={(e) => handleItemChange(item.id, 'itemNumber', e.target.value)}
                    />
                    <input
                      type="text"
                      className="form-input"
                      placeholder="Color"
                      value={item.color}
                      onChange={(e) => handleItemChange(item.id, 'color', e.target.value)}
                    />
                    <input
                      type="number"
                      className="form-input"
                      placeholder="Price"
                      value={item.price}
                      onChange={(e) => handleItemChange(item.id, 'price', e.target.value)}
                    />
                    <input
                      type="number"
                      className="form-input"
                      placeholder="Cost"
                      value={item.cost}
                      onChange={(e) => handleItemChange(item.id, 'cost', e.target.value)}
                    />
                    <input
                      type="number"
                      className="form-input"
                      placeholder="Quantity"
                      value={item.quantity}
                      onChange={(e) => handleItemChange(item.id, 'quantity', e.target.value)}
                    />
                  </div>
                </div>
                <div className="form-group">
                  <label className="form-label">Color Selection</label>
                  <button
                    type="button"
                    className="open-color-modal-button"
                    onClick={() => openColorModal('item', item.id)}
                  >
                    Select Colors
                  </button>
                  {item.selectedColors && item.selectedColors.length > 0 && (
                    <div className="selected-colors">
                      {item.selectedColors.map(color => (
                        <div key={color} className="selected-color-pill">
                          <div
                            className="selected-color-swatch"
                            style={{ backgroundColor: color }}
                          />
                          {colorChart.find(c => c.hex === color)?.name}
                        </div>
                      ))}
                    </div>
                  )}
                </div>
                <div className="form-group">
                  <label className="form-label">Placement</label>
                  <div className="placement-grid">
                    {[...Array(9)].map((_, i) => (
                      <div
                        key={`A${i + 1}`}
                        className={`placement-cell ${item.selectedPlacements && item.selectedPlacements.includes(`A${i + 1}`) ? 'selected' : ''}`}
                        onClick={() => handleItemPlacementClick(item.id, `A${i + 1}`)}
                      >
                        A{i + 1}
                      </div>
                    ))}
                    {[...Array(9)].map((_, i) => (
                      <div
                        key={`H${i + 1}`}
                        className={`placement-cell ${item.selectedPlacements && item.selectedPlacements.includes(`H${i + 1}`) ? 'selected' : ''}`}
                        onClick={() => handleItemPlacementClick(item.id, `H${i + 1}`)}
                      >
                        H{i + 1}
                      </div>
                    ))}
                  </div>
                </div>
                <hr />
              </div>
            ))}
          </div>

          {/* Designs Section */}
          <div className="global-controls">
            <button type="button" className="add-item-button" onClick={addDesign}>
              Add New Design
            </button>
          </div>

          <div className="section">
            <h2 className="section-title">Designs</h2>
            {designs.map((design, index) => (
              <div key={design.id}>
                <div className="item-number">
                  Design #{index + 1}
                  <button type="button" className="remove-button" onClick={() => removeDesign(design.id)}>X</button>
                </div>
                <div className="form-group">
                  <label className="form-label">Design File</label>
                  <div className="upload-area">
                    <p>{design.designFile || 'Click to upload design file or drag and drop'}</p>
                    <input
                      type="file"
                      hidden
                      onChange={(e) => {
                        if (e.target.files && e.target.files[0]) {
                          handleDesignFileChange(design.id, e.target.files[0]);
                        }
                      }}
                    />
                  </div>
                </div>
                <div className="form-group">
                  <label className="form-label">Color Selection</label>
                  <button
                    type="button"
                    className="open-color-modal-button"
                    onClick={() => openColorModal('design', design.id)}
                  >
                    Select Colors
                  </button>
                  {design.selectedColors && design.selectedColors.length > 0 && (
                    <div className="selected-colors">
                      {design.selectedColors.map(color => (
                        <div key={color} className="selected-color-pill">
                          <div
                            className="selected-color-swatch"
                            style={{ backgroundColor: color }}
                          />
                          {colorChart.find(c => c.hex === color)?.name}
                        </div>
                      ))}
                    </div>
                  )}
                </div>
                <div className="form-group">
                  <label className="form-label">Placement</label>
                  <div className="placement-grid">
                    {[...Array(9)].map((_, i) => (
                      <div
                        key={`A${i + 1}`}
                        className={`placement-cell ${design.selectedPlacements && design.selectedPlacements.includes(`A${i + 1}`) ? 'selected' : ''}`}
                        onClick={() => handleDesignPlacementClick(design.id, `A${i + 1}`)}
                      >
                        A{i + 1}
                      </div>
                    ))}
                    {[...Array(9)].map((_, i) => (
                      <div
                        key={`H${i + 1}`}
                        className={`placement-cell ${design.selectedPlacements && design.selectedPlacements.includes(`H${i + 1}`) ? 'selected' : ''}`}
                        onClick={() => handleDesignPlacementClick(design.id, `H${i + 1}`)}
                      >
                        H{i + 1}
                      </div>
                    ))}
                  </div>
                </div>
                <hr />
              </div>
            ))}
          </div>

          <div className="section">
            <h2 className="section-title">Text</h2>
            <div className="form-group">
              <label className="form-label">Font</label>
              <select className="form-select">
                <option>Arial</option>
                <option>Times New Roman</option>
                <option>Helvetica</option>
              </select>
            </div>
          </div>

          <div className="section">
            <h2 className="section-title">Due</h2>
            <div className="time-buttons">
              <button
                type="button"
                className={`time-button ${selectedDueTime === '15min' ? 'selected' : ''}`}
                onClick={() => handleDueTimeSelect('15min')}
              >
                15 min
              </button>
              <button
                type="button"
                className={`time-button ${selectedDueTime === '20min' ? 'selected' : ''}`}
                onClick={() => handleDueTimeSelect('20min')}
              >
                20 min
              </button>
              <button
                type="button"
                className={`time-button ${selectedDueTime === '30min' ? 'selected' : ''}`}
                onClick={() => handleDueTimeSelect('30min')}
              >
                30 min
              </button>
              <button
                type="button"
                className={`time-button ${selectedDueTime === '1hour' ? 'selected' : ''}`}
                onClick={() => handleDueTimeSelect('1hour')}
              >
                1 hour
              </button>
              <input
                type="datetime-local"
                className="form-input"
                value={customDueTime}
                onChange={(e) => {
                  setCustomDueTime(e.target.value);
                  setSelectedDueTime('custom');
                }}
              />
            </div>
          </div>

          <div className="section">
            <h2 className="section-title">Customer Information</h2>
            <div className="grid-container">
              <input
                type="text"
                className="form-input"
                placeholder="Name"
                value={customer.name}
                onChange={(e) => handleCustomerChange('name', e.target.value)}
              />
              <input
                type="tel"
                className="form-input"
                placeholder="Phone Number"
                value={customer.phone}
                onChange={(e) => handleCustomerChange('phone', e.target.value)}
                onBlur={handlePhoneBlur}
              />
              <input
                type="email"
                className="form-input"
                placeholder="Email Address"
                value={customer.email}
                onChange={(e) => handleCustomerChange('email', e.target.value)}
              />
              <input
                type="text"
                className="form-input"
                placeholder="Company"
                value={customer.company}
                onChange={(e) => handleCustomerChange('company', e.target.value)}
              />
            </div>
          </div>

          <div className="section">
            <h2 className="section-title">Payment</h2>
            <div className="payment-options">
              <div
                className={`payment-option ${selectedPayment === 'cash' ? 'selected' : ''}`}
                onClick={() => setSelectedPayment('cash')}
              >
                Cash
              </div>
              <div
                className={`payment-option ${selectedPayment === 'card' ? 'selected' : ''}`}
                onClick={() => setSelectedPayment('card')}
              >
                Card
              </div>
              <div
                className={`payment-option ${selectedPayment === 'check' ? 'selected' : ''}`}
                onClick={() => setSelectedPayment('check')}
              >
                Check
              </div>
              <div
                className={`payment-option ${selectedPayment === 'layaway' ? 'selected' : ''}`}
                onClick={() => setSelectedPayment('layaway')}
              >
                Layaway (50%)
              </div>
              <div
                className={`payment-option ${selectedPayment === 'quote' ? 'selected' : ''}`}
                onClick={() => setSelectedPayment('quote')}
              >
                Quote
              </div>
            </div>
          </div>

          <div className="print-buttons">
            <button type="button" className="print-button">
              Print Receipt
            </button>
            <button type="button" className="print-button">
              Print Barcode
            </button>
            <button type="button" className="print-button">
              Print Order Form
            </button>
          </div>
        </form>
      </div>

      {/* Color Modal */}
      {showColorModal && (
        <div className="color-modal">
          <div className="color-modal-content">
            <button type="button" className="close-button" onClick={closeColorModal}>
              X
            </button>
            <div className="color-chart">
              {colorChart.map(color => (
                <div
                  key={color.hex}
                  className={`color-swatch ${
                    modalFor === 'item'
                      ? (items.find(i => i.id === elementIdForModal)?.selectedColors || []).includes(color.hex)
                      : (designs.find(d => d.id === elementIdForModal)?.selectedColors || []).includes(color.hex)
                      ? 'selected'
                      : ''
                  }`}
                  style={{ backgroundColor: color.hex }}
                  onClick={() =>
                    modalFor === 'item'
                      ? handleItemColorSelect(elementIdForModal, color.hex)
                      : handleDesignColorSelect(elementIdForModal, color.hex)
                  }
                  title={color.name}
                />
              ))}
            </div>
          </div>
        </div>
      )}
    </>
  );
};

export default NewOrder;
